import React from 'react'
import {Line,Pie} from 'react-chartjs-2'

const Grafik = (props)=>{
    let dataCovid = null;
    let data = null;

    if (!props.isGlobal) {
        dataCovid=[2,2,2,2,4,4,6,19,27,34];
        data = {
            labels :["2 Mar","4 Mar","5 Mar","6 Mar","7 Mar","8 Mar","9 Mar","10 Mar","11 Mar"],
            datasets:[
                {
                    label:"Jumlah Positif",
                    fill : false,
                    lineTension:0.1,
                    backgroundColor:'rgba(229,56,50,0.4)',
                    borderColor:'rgba(229,56,56,1)',
                    borderCapStyle:'butt',
                    borderDash:[],
                    borderDashOffset:0.0,
                    borderJoinStyle:'miter',
                    pointBorderColor:'rgba(229,56,56,1)',
                    pointBackgroundColor:'#fff',
                    pointBorderWidth:1,
                    pointHoverRadius:10,
                    pointHoverBackgroundColor:'rgba(229,56,56,1)',
                    pointHoverBorderColor:'rgba(220,220,220,1)',
                    pointBorderWidth:2,
                    pointHitRadius:10,
                    data: dataCovid
                }
            ]
        }
    }

    const dataPie ={
        labels:[
            'Positif',
            'Sembuh',
            'Meninggal'
        ],
        datasets:[{
            data:[2000,12000,300],
            backgroundColor:['#ff6384','#36a2eb','#ffce56'],
            pointHoverBackgroundColor:[
                '#ff6384','#36a2eb','#ffce56'
            ]

        }

        ]
    }
    return (
        <div className="row mb-6">
            <div className="col-lg-12 col-md-12 col-sm-12 col-xl-12">
                <div className="card overflow-hidden bg-white">
                    <div className="card-header">
                        <h3 className="card-title">
                            {props.isGlobal ? "Statistik Virus Corona Di Dunia":"Virus Corona Di Indonesia"}
                        </h3>
                    </div>
                    <div className="card-body">
                        <div className="chart-wrapper">
                            {props.isGlobal ?
                            <Pie data={dataPie}/>:
                            <Line data={data} width={100}
                            height={50}/>
                        }
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Grafik;
